INSERT [dbo].[ORT_POWER_UNIT_TYPE] ([TYPE_ID], [TYPE], [DESCRIPTION], [POLICY_REFERENCE], [DB_CREATE_TIMESTAMP], [DB_CREATE_USERID], [DB_LAST_UPDATE_TIMESTAMP], [DB_LAST_UPDATE_USERID]) VALUES (1, N'Buses/Crummies', N'A motor vehicle used to transport persons, when such transportation is not undertaken for compensation or gain.', N'http://www.th.gov.bc.ca/cvse/ctpm/Chapter_1.pdf', CAST(N'2022-12-07T21:23:23.1566667' AS DateTime2), N'dbo', CAST(N'2022-12-07T21:23:23.1566667' AS DateTime2), N'dbo')
INSERT [dbo].[ORT_POWER_UNIT_TYPE] ([TYPE_ID], [TYPE], [DESCRIPTION], [POLICY_REFERENCE], [DB_CREATE_TIMESTAMP], [DB_CREATE_USERID], [DB_LAST_UPDATE_TIMESTAMP], [DB_LAST_UPDATE_USERID]) VALUES (2, N'Concrete Pumper Trucks', N'Concrete Pumper Trucks are used to pump concrete from a cement mixer truck to where the concrete is actually needed. They travel on the highway at their equipped weight with no load.', N'http://www.th.gov.bc.ca/cvse/ctpm/Chapter_5.pdf', CAST(N'2022-12-07T21:23:23.1566667' AS DateTime2), N'dbo', CAST(N'2022-12-07T21:23:23.1566667' AS DateTime2), N'dbo')
INSERT [dbo].[ORT_POWER_UNIT_TYPE] ([TYPE_ID], [TYPE], [DESCRIPTION], [POLICY_REFERENCE], [DB_CREATE_TIMESTAMP], [DB_CREATE_USERID], [DB_LAST_UPDATE_TIMESTAMP], [DB_LAST_UPDATE_USERID]) VALUES (3, N'Cranes, Rubber-Tired Loaders, Firetrucks - All Terrain', N'Industrial vehicles not designed to transport load on highways.', N'http://www.th.gov.bc.ca/cvse/ctpm/Chapter_5.pdf', CAST(N'2022-12-07T21:23:23.1566667' AS DateTime2), N'dbo', CAST(N'2022-12-07T21:23:23.1566667' AS DateTime2), N'dbo')
INSERT [dbo].[ORT_POWER_UNIT_TYPE] ([TYPE_ID], [TYPE], [DESCRIPTION], [POLICY_REFERENCE], [DB_CREATE_TIMESTAMP], [DB_CREATE_USERID], [DB_LAST_UPDATE_TIMESTAMP], [DB_LAST_UPDATE_USERID]) VALUES (4, N'Cranes, Rubber-Tired Loaders, Firetrucks - Mobile', N'Industrial vehicles not designed to transport load on highways.', N'http://www.th.gov.bc.ca/cvse/ctpm/Chapter_5.pdf', CAST(N'2022-12-07T21:23:23.1600000' AS DateTime2), N'dbo', CAST(N'2022-12-07T21:23:23.1600000' AS DateTime2), N'dbo')
INSERT [dbo].[ORT_POWER_UNIT_TYPE] ([TYPE_ID], [TYPE], [DESCRIPTION], [POLICY_REFERENCE], [DB_CREATE_TIMESTAMP], [DB_CREATE_USERID], [DB_LAST_UPDATE_TIMESTAMP], [DB_LAST_UPDATE_USERID]) VALUES (5, N'Double Decker Buses', N'Double Decker buses are used primarily by the tourism industry.', N'http://www.th.gov.bc.ca/cvse/ctpm/Chapter_5.pdf', CAST(N'2022-12-07T21:23:23.1600000' AS DateTime2), N'dbo', CAST(N'2022-12-07T21:23:23.1600000' AS DateTime2), N'dbo')
INSERT [dbo].[ORT_POWER_UNIT_TYPE] ([TYPE_ID], [TYPE], [DESCRIPTION], [POLICY_REFERENCE], [DB_CREATE_TIMESTAMP], [DB_CREATE_USERID], [DB_LAST_UPDATE_TIMESTAMP], [DB_LAST_UPDATE_USERID]) VALUES (6, N'Farm Vehicles', N'A Farm Vehicle is a commercial vehicle owned and operated by a farmer, rancher or market gardener, the use of which is confined to purposes connected with his farm, ranch or market garden, including use for pleasure and is not used in connection with any other business in which the owner may be engaged.', N'http://www.icbc.com/vehicle-registration/specialty-vehicles/Documents/farm-vehicle.pdf', CAST(N'2022-12-07T21:23:23.1600000' AS DateTime2), N'dbo', CAST(N'2022-12-07T21:23:23.1600000' AS DateTime2), N'dbo')
INSERT [dbo].[ORT_POWER_UNIT_TYPE] ([TYPE_ID], [TYPE], [DESCRIPTION], [POLICY_REFERENCE], [DB_CREATE_TIMESTAMP], [DB_CREATE_USERID], [DB_LAST_UPDATE_TIMESTAMP], [DB_LAST_UPDATE_USERID]) VALUES (7, N'Fixed Equipment - Trucks/Graders etc.', N'Fixed Equipment means equipment bolted or welded to the truck or trailer, which was designed to perform certain engineered function(s), e.g. drilling derrick, vacuum bag house, and coil rig, etc., and may or may not include some incidental reducible load required for the intended function of the vehicle (such as the coil tubing in a coil tubing unit).', N'http://www.th.gov.bc.ca/cvse/ctpm/Chapter_5.pdf', CAST(N'2022-12-07T21:23:23.1600000' AS DateTime2), N'dbo', CAST(N'2022-12-07T21:23:23.1600000' AS DateTime2), N'dbo')
INSERT [dbo].[ORT_POWER_UNIT_TYPE] ([TYPE_ID], [TYPE], [DESCRIPTION], [POLICY_REFERENCE], [DB_CREATE_TIMESTAMP], [DB_CREATE_USERID], [DB_LAST_UPDATE_TIMESTAMP], [DB_LAST_UPDATE_USERID]) VALUES (8, N'Intercity Buses (Pulling Pony Trailers)', N'Intercity Buses are vehicles designed to carry more than 15 passengers and equipped with facilities to allow extended travel without stopping.', N'http://www.th.gov.bc.ca/cvse/ctpm/Chapter_5.pdf', CAST(N'2022-12-07T21:23:23.1600000' AS DateTime2), N'dbo', CAST(N'2022-12-07T21:23:23.1600000' AS DateTime2), N'dbo')
INSERT [dbo].[ORT_POWER_UNIT_TYPE] ([TYPE_ID], [TYPE], [DESCRIPTION], [POLICY_REFERENCE], [DB_CREATE_TIMESTAMP], [DB_CREATE_USERID], [DB_LAST_UPDATE_TIMESTAMP], [DB_LAST_UPDATE_USERID]) VALUES (9, N'Logging Trucks', N'Logging Truck is a truck or truck and trailer combination used to haul, in their natural state, green felled or bucked logs or poles.', N'http://www.th.gov.bc.ca/cvse/ctpm/Chapter_5.pdf', CAST(N'2022-12-07T21:23:23.1600000' AS DateTime2), N'dbo', CAST(N'2022-12-07T21:23:23.1600000' AS DateTime2), N'dbo')
INSERT [dbo].[ORT_POWER_UNIT_TYPE] ([TYPE_ID], [TYPE], [DESCRIPTION], [POLICY_REFERENCE], [DB_CREATE_TIMESTAMP], [DB_CREATE_USERID], [DB_LAST_UPDATE_TIMESTAMP], [DB_LAST_UPDATE_USERID]) VALUES (10, N'Logging Trucks - Off-Highway', N'Logging Trucks - Off-Highway are permitted on an occasional basis from manufacturers to/from customers and to/from the bush for repairs etc., at maximum dimensions noted in Policy.', N'http://www.th.gov.bc.ca/cvse/ctpm/Chapter_5.pdf', CAST(N'2022-12-07T21:23:23.1633333' AS DateTime2), N'dbo', CAST(N'2022-12-07T21:23:23.1633333' AS DateTime2), N'dbo')
INSERT [dbo].[ORT_POWER_UNIT_TYPE] ([TYPE_ID], [TYPE], [DESCRIPTION], [POLICY_REFERENCE], [DB_CREATE_TIMESTAMP], [DB_CREATE_USERID], [DB_LAST_UPDATE_TIMESTAMP], [DB_LAST_UPDATE_USERID]) VALUES (11, N'Long Combination Vehicles (LCV) - Rocky Mountain Doubles', N'LCV vehicles for approved carriers and routes only.', N'http://www.th.gov.bc.ca/cvse/ctpm/Chapter_5.pdf', CAST(N'2022-12-07T21:23:23.1633333' AS DateTime2), N'dbo', CAST(N'2022-12-07T21:23:23.1633333' AS DateTime2), N'dbo')
INSERT [dbo].[ORT_POWER_UNIT_TYPE] ([TYPE_ID], [TYPE], [DESCRIPTION], [POLICY_REFERENCE], [DB_CREATE_TIMESTAMP], [DB_CREATE_USERID], [DB_LAST_UPDATE_TIMESTAMP], [DB_LAST_UPDATE_USERID]) VALUES (12, N'Long Combination Vehicles (LCV) - Turnpike Doubles', N'LCV vehicles for approved carriers and routes only.', N'http://www.th.gov.bc.ca/cvse/ctpm/Chapter_5.pdf', CAST(N'2022-12-07T21:23:23.1633333' AS DateTime2), N'dbo', CAST(N'2022-12-07T21:23:23.1633333' AS DateTime2), N'dbo')
INSERT [dbo].[ORT_POWER_UNIT_TYPE] ([TYPE_ID], [TYPE], [DESCRIPTION], [POLICY_REFERENCE], [DB_CREATE_TIMESTAMP], [DB_CREATE_USERID], [DB_LAST_UPDATE_TIMESTAMP], [DB_LAST_UPDATE_USERID]) VALUES (13, N'Long Wheelbase Truck Tractors Exceeding 6.2 m up to 7.25 m', N'Long Wheelbase Truck Tractors Exceeding 6.2 m up to 7.25 are vehicles that do not comply with wheelbase requirements indicated in CTR Appendix B', N'http://www.th.gov.bc.ca/cvse/ctpm/Chapter_5.pdf', CAST(N'2022-12-07T21:23:23.1633333' AS DateTime2), N'dbo', CAST(N'2022-12-07T21:23:23.1633333' AS DateTime2), N'dbo')
INSERT [dbo].[ORT_POWER_UNIT_TYPE] ([TYPE_ID], [TYPE], [DESCRIPTION], [POLICY_REFERENCE], [DB_CREATE_TIMESTAMP], [DB_CREATE_USERID], [DB_LAST_UPDATE_TIMESTAMP], [DB_LAST_UPDATE_USERID]) VALUES (14, N'Oil and Gas - Bed Trucks', N'Bed Truck means a truck tractor equipped with a cargo carrying deck and a winch that is used for self loading and that is located behind the cab.', N'http://www.th.gov.bc.ca/cvse/ctpm/Chapter_5.pdf', CAST(N'2022-12-07T21:23:23.1666667' AS DateTime2), N'dbo', CAST(N'2022-12-07T21:23:23.1666667' AS DateTime2), N'dbo')
INSERT [dbo].[ORT_POWER_UNIT_TYPE] ([TYPE_ID], [TYPE], [DESCRIPTION], [POLICY_REFERENCE], [DB_CREATE_TIMESTAMP], [DB_CREATE_USERID], [DB_LAST_UPDATE_TIMESTAMP], [DB_LAST_UPDATE_USERID]) VALUES (15, N'Oil and Gas - Oilfield Sows', N'Oil and Gas - Oilfield Sows are vehicles that are exceptionally large truck-tractors equipped with a cargo carrying deck and winch located behind the cab used for loading and unloading in the oilfield.', N'http://www.th.gov.bc.ca/cvse/ctpm/Chapter_5.pdf', CAST(N'2022-12-07T21:23:23.1666667' AS DateTime2), N'dbo', CAST(N'2022-12-07T21:23:23.1666667' AS DateTime2), N'dbo')
INSERT [dbo].[ORT_POWER_UNIT_TYPE] ([TYPE_ID], [TYPE], [DESCRIPTION], [POLICY_REFERENCE], [DB_CREATE_TIMESTAMP], [DB_CREATE_USERID], [DB_LAST_UPDATE_TIMESTAMP], [DB_LAST_UPDATE_USERID]) VALUES (16, N'Oil and Gas - Service Rigs', N'Oil and Gas - Service Rigs are fixed equipment oilfield service trucks and includes rathole augers only equiped with heavy front projected crane (must exceed 14,000 kg tare weight).', N'http://www.th.gov.bc.ca/cvse/ctpm/Chapter_5.pdf', CAST(N'2022-12-07T21:23:23.1666667' AS DateTime2), N'dbo', CAST(N'2022-12-07T21:23:23.1666667' AS DateTime2), N'dbo')
INSERT [dbo].[ORT_POWER_UNIT_TYPE] ([TYPE_ID], [TYPE], [DESCRIPTION], [POLICY_REFERENCE], [DB_CREATE_TIMESTAMP], [DB_CREATE_USERID], [DB_LAST_UPDATE_TIMESTAMP], [DB_LAST_UPDATE_USERID]) VALUES (17, N'Picker Truck Tractors', N'Picker Truck Tractors is a heavy duty crane mounted on a truck tractor or truck, considered permanently mounted equipment.', N'http://www.th.gov.bc.ca/cvse/ctpm/Chapter_5.pdf', CAST(N'2022-12-07T21:23:23.1666667' AS DateTime2), N'dbo', CAST(N'2022-12-07T21:23:23.1666667' AS DateTime2), N'dbo')
INSERT [dbo].[ORT_POWER_UNIT_TYPE] ([TYPE_ID], [TYPE], [DESCRIPTION], [POLICY_REFERENCE], [DB_CREATE_TIMESTAMP], [DB_CREATE_USERID], [DB_LAST_UPDATE_TIMESTAMP], [DB_LAST_UPDATE_USERID]) VALUES (18, N'Scrapers', N'A Scraper is a vehicle that is designed and used primarily for grading of highways, earth moving and other construction work on highways and that is not designed or used primarily for the transportation of persons or property, and that is only incidentally operated or moved over a highway.', N'http://www.th.gov.bc.ca/cvse/ctpm/Chapter_5.pdf', CAST(N'2022-12-07T21:23:23.1700000' AS DateTime2), N'dbo', CAST(N'2022-12-07T21:23:23.1700000' AS DateTime2), N'dbo')
INSERT [dbo].[ORT_POWER_UNIT_TYPE] ([TYPE_ID], [TYPE], [DESCRIPTION], [POLICY_REFERENCE], [DB_CREATE_TIMESTAMP], [DB_CREATE_USERID], [DB_LAST_UPDATE_TIMESTAMP], [DB_LAST_UPDATE_USERID]) VALUES (19, N'Tow Vehicles', N'Tow Vehicles means a motor vehicle used exclusively for towing or rendering assistance to other motor vehicles or to vehicles suffering from defects or disability in their means of locomotion.', N'http://www.bclaws.ca/civix/document/id/complete/statreg/30_78#AppendicesAtoK', CAST(N'2022-12-07T21:23:23.1700000' AS DateTime2), N'dbo', CAST(N'2022-12-07T21:23:23.1700000' AS DateTime2), N'dbo')
INSERT [dbo].[ORT_POWER_UNIT_TYPE] ([TYPE_ID], [TYPE], [DESCRIPTION], [POLICY_REFERENCE], [DB_CREATE_TIMESTAMP], [DB_CREATE_USERID], [DB_LAST_UPDATE_TIMESTAMP], [DB_LAST_UPDATE_USERID]) VALUES (20, N'Truck Tractors', N'Truck Tractor is a motor vehicle, having a net weight of more than 4,000 kg, that is equipped with a fifth-wheel coupler or a centre rotatable log bunk mounted on a bolster affixed to the vehicles chassis, and includes an auto carrier with an underslung fifth wheel coupler and a truck tractor with a load box.', N'http://www.th.gov.bc.ca/cvse/ctpm/Chapter_5.pdf', CAST(N'2022-12-07T21:23:23.1700000' AS DateTime2), N'dbo', CAST(N'2022-12-07T21:23:23.1700000' AS DateTime2), N'dbo')
INSERT [dbo].[ORT_POWER_UNIT_TYPE] ([TYPE_ID], [TYPE], [DESCRIPTION], [POLICY_REFERENCE], [DB_CREATE_TIMESTAMP], [DB_CREATE_USERID], [DB_LAST_UPDATE_TIMESTAMP], [DB_LAST_UPDATE_USERID]) VALUES (21, N'Truck Tractors - Stinger Steered', N'Truck Tractors - Stinger Steered are vehicles equipped with a rear mounted underslung fifth wheel and are considered a truck tractor similar to an auto carrier;', N'http://www.bclaws.ca/civix/document/id/complete/statreg/30_78#AppendicesAtoK', CAST(N'2022-12-07T21:23:23.1700000' AS DateTime2), N'dbo', CAST(N'2022-12-07T21:23:23.1700000' AS DateTime2), N'dbo')
